import React from "react";
import api from './api';

class Ranking extends React.Component{
    state = {
        atletas: [],
      }
    
      async componentDidMount(){
        const response = await api.get('atleta/Phil Heath');
        this.setState({atletas: response.data});
      }
    
    
      render(){ 
    
        const {atletas} = this.state;
    
        return (
               <div>
                 <h1 id="ranking-title" class="text-center">Atletas</h1>
                 {atletas.map((atleta, index)=>(
                    <p id="atleta" class="text-center">{index+1}ยบ {atleta.nome} {atleta.pontos} </p>
                 ))}
               </div>
             );
      }
}

export default Ranking;
