import React from "react";
import api from './api';
import {
    Route,
    NavLink,
    HashRouter
  } from "react-router-dom";
import Atleta from "./Atleta";

class Ranking extends React.Component{
    state = {
        atletas: [],
      }
    
      async componentDidMount(){
        const response = await api.get('');
        this.setState({atletas: response.data});
      }

      render(){ 
    
        const {atletas} = this.state;

        return (
            <HashRouter>
               <div>
                 <h1 id="ranking-title" class="text-center">Atletas</h1>
                 {/* {atletas.map((atleta, index)=>(
                   <p><NavLink to={atleta.nome}>{index+1}ยบ {atleta.nome} {atleta.pontos}</NavLink></p>
                 ))} */}
                 <p><NavLink to="/Atleta">at</NavLink></p>
               </div>
               <div className="content">
               {atletas.map((atleta)=>(
                   <Route path="/" component={Atleta}/>
                 ))}
                    
               </div>
               </HashRouter>
             );
      }
}

export default Ranking;
